package com.android.lib.util;import java.lang.reflect.Type;import com.google.gson.Gson;/** * 类描述：json解析工具  * <p> * 创建人：Kevin Lynn * <p> * 创建时间：2013-1-19 下午7:34:12   * <p>      * 修改备注：     * <p> * @version 1.0 * @since 1.0  */public class JsonUtil {		private JsonUtil() {	}		/**	 * Json数据解析	 * 	 * @param json json源串	 * @param cls 存储json的实体类	 * 	 * @return 相应的实体类对象	 */	public static <T> T parse(String json, Type cls) {		try {			Gson gson = new Gson();			return gson.fromJson(json, cls);		} catch (Exception e) {			LogUtil.d(new JsonUtil(), e.toString());		}		return null;	}}